import { Component } from '@angular/core';
//import { Hero } from 'src/hero';
import { HEROES } from '../mock-hero';
import { Hero } from 'src/hero';

@Component({
  selector: 'app-heroes',
  templateUrl: './heroes.component.html',
  styleUrls: ['./heroes.component.css']
})
export class HeroesComponent {
  /*hero:Hero={
    id:1,
    name:'Superman',
    edad:30,
    nombreReal:'Clark Kent',
    nacionalidad:'USA',
    poderes:'Súper fuerza, velocidad, resistencia, agilidad, reflejos, durabilidad, sentidos y longevidad.',
    creador:'Jerry Siegel'
  }*/

  heroes=HEROES

  selectedHero?:Hero;
  onMouseDownHero?:Hero
  onMouseMoveHero?:Hero
  onMouseOutHero?:Hero
  onMouseOverHero?:Hero
  onMouseUpHero?:Hero
  onSelect(hero:Hero):void{
    this.selectedHero = hero;
    console.log("Selected hero:", hero);
  }
  onMouseDown(hero:Hero):void{
    this.onMouseDownHero = hero;
    console.log("On Mouse Down hero:", hero);
  }
  OnMouseMove(hero:MouseEvent):void{
    const mouseX = hero.clientX;
    const mouseY = hero.clientY;
    console.log("OMouse movido a: X=${mouseX}, Y=${mouseY}");
  }
  nMouseOut(hero:Hero):void{
    this.onMouseOutHero = hero;
    console.log("On Mouse Out hero:", hero);
  }
  OnMouseOver(hero:Hero):void{
    this.onMouseOverHero = hero;
    console.log("On Mouse Over hero:", hero);
  }
  OnMouseUp(hero:Hero):void{
    this.onMouseUpHero = hero;
    console.log("On Mouse UpHero hero:", hero);
  }
}
